import { useState } from "react";
import PropTypes from "prop-types";

import logo from "./media/gLogo.png";
import portrait from "./media/gPortrait.jpeg";

const media = [
   {
      id: 1,
      title: "Media 1",
      description: "Media 1",
      url: "0hOTJJyKVBM",
   },
   {
      id: 2,
      title: "Media 2",
      description: "Media 2",
      url: "0hOTJJyKVBM",
   },
   {
      id: 3,
      title: "Media 3",
      description: "Media 3",
      url: "0hOTJJyKVBM",
   },
];

function Main({ children }) {
   return <div>{children}</div>;
}

function Box({ children }) {
   return (
      <div className="container relative mb-72 left-1/2 -translate-x-1/2">
         {children}
      </div>
   );
}

function ShowLogo() {
   return (
      <img src={logo} alt="Angelina Ortiz Cellist logo" className="-m-20" />
   );
}

function NavBar() {
   const [isOpen, setIsOpen] = useState(false);
   const genericHamburgerLine = `h-1 w-6 my-1 rounded-full bg-white transition ease transform duration-300`;
   return (
      <nav className="">
         <div className="flex justify-center ">
            <ShowLogo />

            {/* <h1 className="hidden md:flex space-x-8 items-center text-4xl">
              Gina Z Photography
           </h1> */}
         </div>
         <div className="flex justify-around items-center">
            {/* xs and small nav */}
            <button
               className="flex flex-col h-12 w-12 border-2 border-white rounded justify-center items-center group md:hidden m-2"
               onClick={() => setIsOpen(!isOpen)}
            >
               <div
                  className={`${genericHamburgerLine} ${
                     isOpen
                        ? "rotate-45 translate-y-3 opacity-50 group-hover:opacity-100"
                        : "opacity-50 group-hover:opacity-100"
                  }`}
               />
               <div
                  className={`${genericHamburgerLine} ${
                     isOpen ? "opacity-0" : "opacity-50 group-hover:opacity-100"
                  }`}
               />
               <div
                  className={`${genericHamburgerLine} ${
                     isOpen
                        ? "-rotate-45 -translate-y-3 opacity-50 group-hover:opacity-100"
                        : "opacity-50 group-hover:opacity-100"
                  }`}
               />
            </button>
            {isOpen && (
               <>
                  <ul className="flex items-center gap-3 md:hidden">
                     <li>
                        <a href="#media" onClick={() => setIsOpen(!isOpen)}>
                           Media
                        </a>
                     </li>
                     <li>
                        <a href="#contact" onClick={() => setIsOpen(!isOpen)}>
                           Contact
                        </a>
                     </li>
                     <li>
                        <a href="#about" onClick={() => setIsOpen(!isOpen)}>
                           About
                        </a>
                     </li>
                     {/* <li>
                       <a href="#blog" onClick={() => setIsOpen(!isOpen)}>
                          Blog
                       </a>
                    </li>
                    <li>
                       <a href="#reviews" onClick={() => setIsOpen(!isOpen)}>
                          Reviews
                       </a>
                    </li> */}
                  </ul>
               </>
            )}
            {/* md and large nav */}
            <ul className="hidden text-xl md:flex space-x-8">
               <li className="hover:bg-white hover:text-black hover:px-2 hover:font-bold">
                  <a href="#media">Media</a>
               </li>
               <li className="hover:bg-white hover:text-black hover:px-2 hover:font-bold">
                  <a href="#contact">Contact</a>
               </li>
               <li className="hover:bg-white hover:text-black hover:px-2 hover:font-bold">
                  <a href="#about">About</a>
               </li>
               {/* <li className="hover:bg-white hover:text-black hover:px-2 hover:font-bold">
                 <a href="#blog">Blog</a>
              </li>
              <li className="hover:bg-white hover:text-black hover:px-2 hover:font-bold">
                 <a href="#reviews">Reviews</a>
              </li> */}
            </ul>
         </div>
      </nav>
   );
}

function ScrollToTop() {
   return (
      <div className="flex justify-center items-center">
         <a
            href="#home"
            className="animate-pulse bg-black text-white text-xs md:text-lg lg:text-2xl px-2 py-1 md:mt-3 rounded hover:bg-white hover:text-black"
         >
            &uarr;
         </a>
      </div>
   );
}

function Home() {
   return (
      <>
         <div>
            <img
               src={portrait}
               alt="Angi plays cello"
               className="w-full md:w-1/2"
            />
         </div>
         <div className="absolute top-0 left-1/2 -translate-x-1/2 text-2xl font-semibold animate-pulse">
            Hi, and welcome!
         </div>
      </>
   );
}

const YoutubeEmbed = ({ embedId, title }) => (
   <div className="">
      <iframe
         src={`https://www.youtube.com/embed/${embedId}`}
         frameBorder="0"
         allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
         allowFullScreen
         title={title}
      />
   </div>
);

YoutubeEmbed.propTypes = {
   embedId: PropTypes.string.isRequired,
};

function Media() {
   return (
      <>
         <div
            id="media"
            className="container mx-auto text-center px-5 py-2 lg:px-32 lg:pt-24"
         >
            <h2 className="text-xl mb-5 font-semibold">
               Juliet - my cello has a name - and I perform.
            </h2>
            <div className="-m-1 flex flex-wrap md:-m-2">
               {media.map((media) => (
                  <div
                     key={media.id}
                     className="flex flex-col w-full sm:w-1/2 md:w-1/3 lg:w-1/4 p-1 md:p-2 "
                  >
                     <p className="text-sm text-gray-600">{media.title}</p>
                     <p className="text-sm text-gray-600">
                        {media.description}
                     </p>
                     <YoutubeEmbed embedId={media.url} title={media.title} />
                  </div>
               ))}
            </div>
         </div>
      </>
   );
}

function Contact() {
   return <div>Contact</div>;
}

function About() {
   return <div>About</div>;
}

export default function App() {
   return (
      <div
         id="home"
         className="grid grid-cols-5 gap-3 bg-black text-white font-primary"
      >
         <div className="col-span-5">
            <NavBar />
         </div>
         <div className="col-span-5">
            <Main>
               <Box>
                  <Home />
               </Box>
               <Box>
                  <Media />
                  <ScrollToTop />
               </Box>
               <Box>
                  <Contact />
                  <ScrollToTop />
               </Box>
               <Box>
                  <About />
                  <ScrollToTop />
               </Box>
            </Main>
         </div>
      </div>
   );
}
