import { useState, useRef } from "react";
import PropTypes from "prop-types";
import ReCAPTCHA from "react-google-recaptcha";

import logo from "./media/gLogo.png";
import portrait from "./media/gPortrait.jpeg";
import candid from "./media/gAtMarket.jpeg";
import fb from "./media/fb.png";
import insta from "./media/insta.png";

const media = [
   {
      id: 1,
      title: "Media 1",
      description: "Media 1",
      url: "0hOTJJyKVBM",
   },
   {
      id: 2,
      title: "Media 2",
      description: "Media 2",
      url: "0hOTJJyKVBM",
   },
   {
      id: 3,
      title: "Media 3",
      description: "Media 3",
      url: "0hOTJJyKVBM",
   },
];

function Main({ children }) {
   return <div>{children}</div>;
}

function Box({ children }) {
   return (
      <div className="container relative mb-72 left-1/2 -translate-x-1/2">
         {children}
      </div>
   );
}

function ShowLogo() {
   return (
      <img src={logo} alt="Angelina Ortiz Cellist logo" className="-m-20" />
   );
}

function NavBar() {
   const [isOpen, setIsOpen] = useState(false);
   const genericHamburgerLine = `h-1 w-6 my-1 rounded-full bg-white transition ease transform duration-300`;
   return (
      <nav className="">
         <div className="flex justify-center ">
            <ShowLogo />

            {/* <h1 className="hidden md:flex space-x-8 items-center text-4xl">
              Gina Z Photography
           </h1> */}
         </div>
         <div className="flex justify-around items-center">
            {/* xs and small nav */}
            <button
               className="flex flex-col h-12 w-12 border-2 border-white rounded justify-center items-center group md:hidden m-2"
               onClick={() => setIsOpen(!isOpen)}
            >
               <div
                  className={`${genericHamburgerLine} ${
                     isOpen
                        ? "rotate-45 translate-y-3 opacity-50 group-hover:opacity-100"
                        : "opacity-50 group-hover:opacity-100"
                  }`}
               />
               <div
                  className={`${genericHamburgerLine} ${
                     isOpen ? "opacity-0" : "opacity-50 group-hover:opacity-100"
                  }`}
               />
               <div
                  className={`${genericHamburgerLine} ${
                     isOpen
                        ? "-rotate-45 -translate-y-3 opacity-50 group-hover:opacity-100"
                        : "opacity-50 group-hover:opacity-100"
                  }`}
               />
            </button>
            {isOpen && (
               <>
                  <ul className="flex items-center gap-3 md:hidden">
                     <li>
                        <a href="#media" onClick={() => setIsOpen(!isOpen)}>
                           Media
                        </a>
                     </li>
                     <li>
                        <a href="#contact" onClick={() => setIsOpen(!isOpen)}>
                           Contact
                        </a>
                     </li>
                     <li>
                        <a href="#about" onClick={() => setIsOpen(!isOpen)}>
                           About
                        </a>
                     </li>
                     {/* <li>
                       <a href="#blog" onClick={() => setIsOpen(!isOpen)}>
                          Blog
                       </a>
                    </li>
                    <li>
                       <a href="#reviews" onClick={() => setIsOpen(!isOpen)}>
                          Reviews
                       </a>
                    </li> */}
                  </ul>
               </>
            )}
            {/* md and large nav */}
            <ul className="hidden text-xl md:flex space-x-8">
               <li className="hover:bg-white hover:text-black hover:px-2 hover:font-bold">
                  <a href="#media">Media</a>
               </li>
               <li className="hover:bg-white hover:text-black hover:px-2 hover:font-bold">
                  <a href="#contact">Contact</a>
               </li>
               <li className="hover:bg-white hover:text-black hover:px-2 hover:font-bold">
                  <a href="#about">About</a>
               </li>
               {/* <li className="hover:bg-white hover:text-black hover:px-2 hover:font-bold">
                 <a href="#blog">Blog</a>
              </li>
              <li className="hover:bg-white hover:text-black hover:px-2 hover:font-bold">
                 <a href="#reviews">Reviews</a>
              </li> */}
            </ul>
         </div>
      </nav>
   );
}

function ScrollToTop() {
   return (
      <div className="flex justify-center items-center">
         <a
            href="#home"
            className="animate-pulse bg-black text-white text-xs md:text-lg lg:text-2xl px-2 py-1 md:mt-3 rounded hover:bg-white hover:text-black"
         >
            &uarr;
         </a>
      </div>
   );
}

function Home() {
   return (
      <>
         <div>
            <img
               src={portrait}
               alt="Angi plays cello"
               className="w-full md:w-1/2"
            />
         </div>
         <div className="absolute top-0 left-1/2 -translate-x-1/2 text-2xl font-semibold animate-pulse">
            Hi, and welcome!
         </div>
      </>
   );
}

const YoutubeEmbed = ({ embedId, title }) => (
   <div className="">
      <iframe
         src={`https://www.youtube.com/embed/${embedId}`}
         frameBorder="0"
         allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
         allowFullScreen
         title={title}
      />
   </div>
);

YoutubeEmbed.propTypes = {
   embedId: PropTypes.string.isRequired,
};

function Media() {
   return (
      <>
         <div
            id="media"
            className="container mx-auto text-center px-5 py-2 lg:px-32 lg:pt-24"
         >
            <h2 className="text-xl mb-5 font-semibold">
               Juliet - my cello has a name - and I perform.
            </h2>
            <div className="-m-1 flex flex-wrap md:-m-2">
               {media.map((media) => (
                  <div
                     key={media.id}
                     className="flex flex-col w-full sm:w-1/2 md:w-1/3 lg:w-1/4 p-1 md:p-2 "
                  >
                     <p className="text-sm text-gray-600">{media.title}</p>
                     <p className="text-sm text-gray-600">
                        {media.description}
                     </p>
                     <YoutubeEmbed embedId={media.url} title={media.title} />
                  </div>
               ))}
            </div>
         </div>
      </>
   );
}

function Contact() {
   const [success, setSuccess] = useState("");
   const [error, setError] = useState("");
   const [fullName, setFullName] = useState("");
   const [email, setEmail] = useState("");
   const [message, setMessage] = useState("");
   const [hasCaptchaToken, setHasCaptchaToken] = useState(false);

   const SECRET_KEY = "6LeIxAcTAAAAAJcZVRqyHh71UMIEGNQ_MXjiZKhI";

   const captchaRef = useRef(null);

   const handleSubmit = async (e) => {
      e.preventDefault();

      const formDataObj = Object.fromEntries(new FormData(e.target).entries());

      // data validation
      const fullNameRegX =
         /([A-Z]+([']|[\.])?([A-Z,a-z,\.]*))\s(([A-Z]|['])+[A-Z,a-z]*)([-\s](([A-Z]|['])+[A-Z,a-z]*)*)?/g;

      if (fullNameRegX.test(formDataObj.name)) {
         // setError("");
         if (formDataObj.message.length > 3) {
            // setError("");
            if (hasCaptchaToken) {
               const token = captchaRef.current.getValue(); // returns token from ReCaptcha component

               captchaRef.current.reset();

               const raw = JSON.stringify({
                  ...formDataObj,
                  "g-recaptcha-response": token,
               });

               const options = {
                  method: "POST",
                  headers: {
                     "Content-Type": "application/json",
                  },
                  body: raw,
               };

               try {
                  // Contact test API
                  const response = await fetch(
                     "https://ydvvjbdup4.execute-api.us-east-1.amazonaws.com/Prod",
                     options
                  );

                  if (response.ok) {
                     // show success message
                     setSuccess("Your message was sent!");
                     setError("");

                     // reset form
                     setFullName("");
                     setEmail("");
                     setMessage("");
                     setHasCaptchaToken(false);

                     // const data = await response.json(); // the data is a message Id string, good for nothing
                  } else {
                     // show error message
                     setError(
                        "Sorry, something went wrong trying to send your message. The administrator has been informed. Please try again at a later time."
                     );
                     console.log(response);
                  }
               } catch (error) {
                  setError(
                     "Sorry, something went wrong trying to send your message. The administrator has been informed. Please try again at a later time."
                  );
               }
            } else {
               // the recaptcha was not checked
               setError("Please check the recaptcha box.");
            }
         } else {
            setError("Please enter a valid message.");
         }
      } else {
         setError("Please enter a valid full name.");
      }
   };

   function handleOnChange(value) {
      setHasCaptchaToken(true);
      setError("");
   }

   return (
      <div
         id="contact"
         className="mt-60  pt-10 grid grid-cols-1 content-start justify-items-center"
      >
         <h2 className="text-3xl font-bold text-center m-5 ">Contact me!</h2>

         <div className="m-2 p-5  ">
            {/* <!-- Contact form --> */}
            <form className="px-5" id="contact-us-form" onSubmit={handleSubmit}>
               {success.length > 0 && (
                  <h3 className="text-green-500  text-center font-bold mb-5">
                     {success}
                  </h3>
               )}
               {error.length > 0 && (
                  <h3 className="text-red-500 text-center font-bold mb-5">
                     {error}
                  </h3>
               )}

               <label htmlFor="name" className="block my-3">
                  <span className="block text-sm font-medium">
                     Your Full Name
                  </span>
                  <input
                     type="text"
                     className="text-black pl-3"
                     id="name"
                     name="name"
                     placeholder="Enter your full name"
                     autoComplete="name"
                     value={fullName}
                     onChange={(e) => setFullName(e.target.value)}
                  />
               </label>
               <label className="block my-3">
                  <span className="block text-sm font-medium">Your Email</span>
                  <input
                     type="email"
                     className="peer text-black pl-3 pr-9"
                     name="email"
                     id="email"
                     value={email}
                     onChange={(e) => setEmail(e.target.value)}
                  />
                  <p id="emailHelp" className="text-xs italic">
                     We'll never share your email with anyone else.
                  </p>
                  <p className="mt-2 invisible peer-invalid:visible text-pink-600 text-sm">
                     Please provide a valid email address.
                  </p>
               </label>
               <label htmlFor="message" className="my-3">
                  <span className="block text-sm font-medium">
                     Your message
                  </span>
                  <textarea
                     className="text-black pl-3 pr-12"
                     id="message"
                     name="message"
                     rows="4"
                     value={message}
                     onChange={(e) => setMessage(e.target.value)}
                  ></textarea>
               </label>
               <div className="my-3">
                  <ReCAPTCHA
                     sitekey={SECRET_KEY}
                     ref={captchaRef}
                     onChange={handleOnChange}
                  />
               </div>
               <div className="mb-24">
                  <button
                     type="submit"
                     id="submitBtn"
                     className="rounded bg-white text-black font-bold py-2 px-4 my-3 hover:text-white hover:bg-gray-700 animate-pulse"
                     hidden={!hasCaptchaToken ? "hidden" : ""}
                  >
                     Send Message!
                  </button>
               </div>
            </form>
         </div>
      </div>
   );
}

function About() {
   return (
      <div id="about" className=" text-center px-5 py-2 lg:px-32 lg:pt-24 ">
         <h2 className="text-xl mb-5 font-semibold">About Me</h2>
         <h3 className="text-lg mb-5 font-semibold">Cellist for 8 years.</h3>
         <div className="relative left-1/2 -translate-x-1/2 md:w-1/2">
            <img src={candid} alt="Angelina playing at a farmer's market" />
         </div>
         <p className="p-2">
            Hi! I'm Angelina and I'm so excited you visited my website! I have a
            passion for music, performing with Juliet and singing too! I enjoy
            spreading love through music.&nbsp;
            <a href="#contact" className="hover:bg-black hover:text-white">
               Contact
            </a>{" "}
            me if you have any questions. Visit me on my social media. I hope to
            hear from you soon!
         </p>
         <div className="flex flex-row justify-center">
            <a
               href="https://www.facebook.com/angelwingsortiz/"
               target="_blank"
               rel="noreferrer"
            >
               <img src={fb} alt="" />
            </a>
            <a
               href="https://www.instagram.com/angelinalacherie/"
               target="_blank"
               rel="noreferrer"
            >
               <img src={insta} alt="" />
            </a>
         </div>
      </div>
   );
}

export default function App() {
   return (
      <div
         id="home"
         className="grid grid-cols-5 gap-3 bg-black text-white font-primary"
      >
         <div className="col-span-5">
            <NavBar />
         </div>
         <div className="col-span-5">
            <Main>
               <Box>
                  <Home />
               </Box>
               <Box>
                  <Media />
                  <ScrollToTop />
               </Box>
               <Box>
                  <Contact />
                  <ScrollToTop />
               </Box>
               <Box>
                  <About />
                  <ScrollToTop />
               </Box>
            </Main>
         </div>
      </div>
   );
}
