import { useState, useRef } from "react";
import PropTypes from "prop-types";
import ReCAPTCHA from "react-google-recaptcha";

import logo from "./media/gLogo.png";
import portrait from "./media/gPortrait.jpeg";
import candid from "./media/gAtMarket.jpeg";
import fb from "./media/fb.png";
import insta from "./media/insta.png";

const media = [
   {
      id: 1,
      title: "Media 1",
      description: "Media 1",
      url: "0hOTJJyKVBM",
   },
   {
      id: 2,
      title: "Media 2",
      description: "Media 2",
      url: "0hOTJJyKVBM",
   },
   {
      id: 3,
      title: "Media 3",
      description: "Media 3",
      url: "0hOTJJyKVBM",
   },
];

function Main({ children }) {
   return <div>{children}</div>;
}

function Box({ children }) {
   return (
      <div className="container relative mb-96 left-1/2 -translate-x-1/2">
         {children}
      </div>
   );
